  sixcount = handle.select { |i| i.length == 6 }
  sevencount = handle.select { |i| i.length == 7 }
  eightcount = handle.select { |i| i.length == 8 }
  ninecount = handle.select { |i| i.length == 9 }
  tencount = handle.select { |i| i.length == 10 }
  elevencount = handle.select { |i| i.length == 11 }
  twelvecount = handle.select { |i| i.length == 12 }
  thirteencount = handle.select { |i| i.length == 13 }
  fourteencount = handle.select { |i| i.length == 14 }
  fifteencount = handle.select { |i| i.length == 15 }
  sixteencount = handle.select { |i| i.length == 16 }

  puts "1 caractères :#{onecount.count}"
  puts "2 caractères :#{twocount.count}"
  puts "3 caractères :#{threecount.count}"
  puts "4 caractères :#{fourcount.count}"
  puts "5 caractères :#{fivecount.count}"
  puts "6 caractères :#{sixcount.count}"
  puts "7 caractères :#{sevencount.count}"
