  puts "9 caractères :#{ninecount.count}"
  puts "10 caractères :#{tencount.count}"
  puts "11 caractères :#{elevencount.count}"
  puts "12 caractères :#{twelvecount.count}"
  puts "13 caractères :#{thirteencount.count}"
  puts "14 caractères :#{fourteencount.count}"
  puts "15 caractères :#{fifteencount.count}"
  puts "16 caractères :#{sixteencount.count}"
  puts ""

end
